import React, { useState } from 'react';
import { fetchData } from '../api';

const HealthForm = () => {
    const [formData, setFormData] = useState({
        age: '',
        height: '',
        weight: '',
        activityLevel: '',
        gender: '',
        dailyWaterIntake: '',
        sleepHours: '',
        stressLevel: '',
    });

    const handleChange = (name, value) => {
        setFormData({
            ...formData,
            [name]: value
        });
    };

    const handleSubmit = async () => {
        const data = await fetchData('/predict', formData);
        console.log('Erhaltene Daten:', data);
        // Hier kannst du die erhaltenen Daten weiter verarbeiten oder anzeigen
    };

    return (
        <div style={styles.formContainer}>
            <h2 style={styles.heading}>Health Assessment Form</h2>

            {Object.keys(formData).map((key) => (
                <div key={key} style={styles.inputGroup}>
                    <input
                        type={key === 'gender' ? 'text' : 'number'}
                        style={styles.input}
                        value={formData[key]}
                        onChange={e => handleChange(key, e.target.value)}
                        placeholder=" "
                    />
                    <label style={styles.label}>{formatLabel(key)}</label>
                </div>
            ))}

            <button style={styles.button} onClick={handleSubmit}>Absenden</button>
        </div>
    );
};

const formatLabel = (key) => {
    switch (key) {
        case 'age':
            return 'Alter';
        case 'height':
            return 'Größe (cm)';
        case 'weight':
            return 'Gewicht (kg)';
        case 'activityLevel':
            return 'Aktivitätslevel';
        case 'gender':
            return 'Geschlecht';
        case 'dailyWaterIntake':
            return 'Tägliche Wasseraufnahme (Gläser)';
        case 'sleepHours':
            return 'Schlafstunden';
        case 'stressLevel':
            return 'Stresslevel (1-10)';
        default:
            return key;
    }
};

const styles = {
    formContainer: {
        maxWidth: '600px',
        margin: '50px auto',
        padding: '30px',
        borderRadius: '12px',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
        backgroundColor: '#ffffff',
        transition: 'transform 0.2s',
    },
    heading: {
        textAlign: 'center',
        marginBottom: '30px',
        fontSize: '28px',
        color: '#333',
        textTransform: 'uppercase',
        letterSpacing: '1px',
    },
    inputGroup: {
        position: 'relative',
        marginBottom: '30px',
    },
    input: {
        width: '100%',
        padding: '15px 10px',
        borderRadius: '4px',
        border: '1px solid #ccc',
        fontSize: '16px',
        boxSizing: 'border-box',
        transition: 'border-color 0.3s, box-shadow 0.3s',
        outline: 'none',
        background: 'rgba(255, 255, 255, 0.9)',
    },
    inputFocus: {
        borderColor: '#007BFF',
        boxShadow: '0 0 5px rgba(0, 123, 255, 0.5)',
    },
    label: {
        position: 'absolute',
        top: '-10px',
        left: '10px',
        backgroundColor: '#ffffff',
        padding: '0 5px',
        fontSize: '14px',
        color: '#666',
        pointerEvents: 'none',
        transition: '0.2s ease all',
    },
    button: {
        width: '100%',
        padding: '15px',
        borderRadius: '4px',
        border: 'none',
        backgroundColor: '#007BFF',
        color: '#fff',
        fontSize: '18px',
        cursor: 'pointer',
        textTransform: 'uppercase',
        fontWeight: 'bold',
        letterSpacing: '1px',
        transition: 'background-color 0.3s, transform 0.2s',
    },
    buttonHover: {
        backgroundColor: '#0056b3',
        transform: 'translateY(-3px)',
    }
};

export default HealthForm;
